char *EXAMPLE_DIRECTIONS = "ULL\nRRDDD\nLURDL\nUUUUD\n";

char *DIRECTIONS = "\\\\\n";

#include <stdio.h>
#include <string.h>

// 1 2 3
// 4 5 6
// 7 8 9

int X = 1;
int Y = 1;

int PAD[3][3] = {1, 2, 3, 4, 5, 6, 7, 8, 9};

void up(int *y) {
    if (*y <= 0) return; /* boundary absorbtion no-op */
    (*y)--;
}

void down(int *y) {
    if (*y >= 2) return; /* boundary absorbtion no-op */
    (*y)++;
}

void left(int *x) {
    if (*x <= 0) return; /* &c. */
    (*x)--;
}

void right(int *x) {
    if (*x >= 2) return;
    (*x)++;
}

void key_step(char direction) {
    if (direction == 'U') {
        up(&Y);
    } else if (direction == 'D') {
        down(&Y);
    } else if (direction == 'L') {
        left(&X);
    } else if (direction == 'R') {
        right(&X);
    } else if (direction == '\n') {
        printf("%d", PAD[Y][X]);
    }
}

int main() {
    int example_len = strlen(EXAMPLE_DIRECTIONS);
    for (int i = 0; i < example_len; i++) {
        key_step(EXAMPLE_DIRECTIONS[i]);
    }
    printf("\n");

    int directions_len = strlen(DIRECTIONS);
    for (int i = 0; i < directions_len; i++) {
        key_step(DIRECTIONS[i]);
    }
    printf("\n");
}
